Âú® Query Tree ‰∏≠ÂèØ‰ª•ÁúãÂá∫ÊÄéÈ∫ºÊ±ÇÂá∫Ë©≤Á¥¢ÂºïÁöÑÂâçÁ∂¥„ÄÇÊü•Ë©¢ÊüêÁ¥¢ÂºïÁöÑÂâçÁ∂¥ÊôÇÔºåÂõûÂÇ≥ÁöÑÂÄºÊúÉÊòØË©≤Á¥¢ÂºïÂ∞çÊáâÁØÄÈªûÁöÑÂÄºÊ≤øËëóÈÇäÊääÊâÄÊúâÁ•ñÂÖà (ancestor) ÁöÑÂÄºÁõ∏Âä†„ÄÇ‰æãÂ¶ÇË¶ÅÊ±ÇÁ¥¢Âºï 7 ÁöÑÂâçÁ∂¥ÔºåÂõûÂÇ≥ÁöÑÂÄºÊúÉÊòØÁØÄÈªû 4„ÄÅ6„ÄÅ7 ÁöÑÁõ∏Âä†„ÄÇ&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;ÁØÄÈªû i ÁöÑÁà∂ÁØÄÈªûÊòØ i+lowbit (i)&lt;/li&gt;
&lt;/ul&gt;
&lt;h1 id=&#34;Á®ãÂºèÁ¢º&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#Á®ãÂºèÁ¢º&#34;&gt;#&lt;/a&gt; Á®ãÂºèÁ¢º&lt;/h1&gt;
&lt;pre&gt;&lt;code class=&#34;language-cpp=&#34;&gt;#include &amp;lt;bits/stdc++.h&amp;gt;
#define bit_capacity 100001

class bit&amp;#123;
    public:
        int length;
        // Êõ¥Êñ∞ÂÖÉÁ¥†ÂÄº
        void update(int i,int delta);
        // ÂâçÁ∂¥Âíå
        int query(int i);
        // ÂçÄÈñìÊü•Ë©¢
        int query(int f,int l);
        // ÂØ¶ÈöõÂÑ≤Â≠òÁöÑÈô£Âàó
        int *bitree=index;
    private:
        int lowbit(int x) &amp;#123;return x&amp;amp;(-x);&amp;#125;
        int index[bit_capacity];
&amp;#125;;

void bit::update(int i,int delta)&amp;#123;
    while(i&amp;lt;=length)&amp;#123;
        index[i]+=delta;
        i+=lowbit(i);
    &amp;#125;
&amp;#125;

int bit::query(int i)&amp;#123;
    int sum =0;
    while(i&amp;gt;0)&amp;#123;
        sum+=index[i];
        i-=lowbit(i);
    &amp;#125;
    return sum;
&amp;#125;
int bit::query(int f,int l)&amp;#123;
    return query(l)-query(f);
&amp;#125;

int main()&amp;#123;
    bit tree;
    tree.length=10;
    for(int i=1;i&amp;lt;=10;i++) tree.update(i,i);
    for(int i=1;i&amp;lt;=10;i++)&amp;#123;
        std::cout&amp;lt;&amp;lt;&amp;quot;BIT[&amp;quot;&amp;lt;&amp;lt;i&amp;lt;&amp;lt;&amp;quot;]=&amp;quot;&amp;lt;&amp;lt;tree.bitree[i]&amp;lt;&amp;lt;std::endl;
   &amp;#125;
    return 0;
&amp;#125;
